					change_attr(clone_selector(6), qty_sold_id)
					change_attr(clone_selector(7), est_unique_buyers_id)
					change_attr(clone.find(route).siblings('input'), csrf_id)

					$('.tbody').append("<div class='product_separator separator'></div>")
					$('.tbody').append(clone)

